mapper.insert:
INSERT INTO role_permission(
	app_id,
	role_id,
	permission_id
) 
VALUES (
	#{appId},
	#{roleId},
	#{permissionId}
)

mapper.delete:
DELETE FROM role_permission
WHERE 1 = 1

	AND permission_id in #{item}

DELETE FROM role_permission
WHERE 1 = 1

	AND role_id in #{item}

DELETE FROM role_permission
WHERE 1 = 1

	AND app_id in #{item}

DELETE FROM role_permission
WHERE 1 = 1
AND app_id = #{appId}
AND role_id = #{roleId}
